mapper.select:
SELECT OWN_CD as ownCd
FROM TBL_OWN
WHERE USER_CD = #{code}

SELECT ADDR_S_D_CD AS addrCd2 , ITEM AS addr2
FROM TBL_ADDR_S_G_G
WHERE ADDR_S_D_CD = #{addrCd1}

SELECT ADDR_S_D_CD AS addrCd3 , ITEM AS addr3
FROM TBL_ADDR_D_E_M
WHERE ADDR_S_D_CD = #{addrCd2}

SELECT COUNT(*)
FROM VIEW_R_NOTE_LIST
WHERE RECEIVER = #{code}

mapper.update:
UPDATE TBL_NOTE
SET R_DEL_DATE = SYSDATE
WHERE NOTE_CD = #{noteCd}

mapper.select:
SELECT NICKNAME AS nickName, CONTENT AS content
FROM VIEW_R_NOTE_LIST
WHERE NOTE_CD = #{noteCd}

SELECT WALKLASTCD AS walkLastCd , WALKCD2 AS walkCd2 , OWNCD2 AS ownCd2 , WALKCD AS walkCd , OWNCD AS ownCd1 , WALKDATE AS walkDate , STARTTIME AS startTime , ENDTIME AS endTime , NICKNAME AS nickName , AGE AS age , NVL(PHOTO, 'none') AS photo , GENDER AS gender , INTR AS intr , INTRDETAIL AS intrDetail , PETCOUNT AS petCount FROM ( SELECT * FROM VIEW_WALK_LAST_DETAIL_OWN_SEC WHERE OWNCD2 = FUNC_GET_OWN_CD(#{userCd}) AND WALKLASTCD = #{walkLastCd }
UNION
SELECT * FROM VIEW_WALK_LAST_DETAIL_OWN_FIR WHERE OWNCD = FUNC_GET_OWN_CD(#{userCd}) AND WALKLASTCD = #{walkLastCd } )

mapper.delete:
DELETE TBL_WALK
WHERE WALK_CD2 = #{walkCd}
  AND OWN_CD = #{ownCd}

mapper.select:
SELECT WOK_CD as wokCd
FROM TBL_AGC_SEL
WHERE AGC_SEL_CD = #{agcCd}

SELECT AGC_CD as agcCd
FROM TBL_AGC_SEL
WHERE AGC_SEL_CD = #{agcCd}

SELECT MSG as msg
FROM TBL_AGC_SEL
WHERE AGC_SEL_CD = #{agcCd}

SELECT SCH_ITEM_CD as schItemCd
FROM VIEW_REC_LIST
WHERE REC_CD = #{recCd}

SELECT ACC_CD as accCd
FROM VIEW_REC_LIST
WHERE REC_CD = #{recCd}

mapper.delete:
DELETE FROM

	TBL_WALK_REC
	WHERE WALK_REC_CD = #{recCd}


	TBL_EDU_REC
	WHERE EDU_REC_CD = #{recCd}


	TBL_BUT_REC
	WHERE BUT_REC_CD = #{recCd}


	TBL_HEL_REC
	WHERE HEL_REC_CD = #{recCd}


	TBL_SUP_REC
	WHERE SUP_REC_CD = #{recCd}


	TBL_ETC_REC
	WHERE ETC_REC_CD = #{recCd}

mapper.select:
{
   CALL PRC_OWN_CAL_UPDATE
   (
      #{schCd},
      #{walkDate}
   )
}
